export async function register(
  _: UTSJSONObject | null = null
): Promise<UTSJSONObject> {
  _consent = true;
  try {
    DeviceIdNative.getIDFAOrNull(true);
  } catch {}
  return { consent: _consent } as UTSJSONObject;
